import React, { useContext } from 'react'
import './Cart.css'
import { ShopContext } from '../Context/ShopContext'
import remove from '../Components/Assest/Ecommerce_Frontend_Assets/Assets/cart_cross_icon.png'

export const Cart = () => {
  const { all_product, cartItems, removeFromCart } = useContext(ShopContext);

  return (
    <div className='cart'>
      {/* Cart Header */}
      <div className="main">
        <p>Products</p>
        <p>Title</p>
        <p>Price</p>
        <p>Quantity</p>
        <p>Total</p>
        <p>Remove</p>
      </div>
      <hr />

      {/* Cart Items */}
      {all_product.map((e) => {
        if (cartItems[e.id] > 0) {
          return (
            <div key={e.id}>
              <div className="format">
                {/* Product image */}
                <img src={e.image} alt={e.name} className='carticon_product_icon' />

                {/* Product title */}
                <p>{e.name}</p>

                {/* Product price */}
                <p>${e.new_price}</p>

                {/* Quantity */}
                <button className='quantity'>{cartItems[e.id]}</button>

                {/* Total price for that product */}
                <p>${e.new_price * cartItems[e.id]}</p>

                {/* Remove button */}
                <img
                  src={remove}
                  onClick={() => removeFromCart(e.id)}
                  alt="Remove"
                  className="remove-icon"
                />
              </div>
              <hr />
            </div>
          )
        }
        return null; // prevents "undefined" warnings
      })}
    </div>
  )
}
