import React, { useContext, useState } from "react";
import { ShopContext } from "../Context/ShopContext";
import "./AdminPage.css";

const AdminPage = () => {
  const { products, addProduct, deleteProduct } = useContext(ShopContext);

  const [newProduct, setNewProduct] = useState({
    name: "",
    price: "",
    image: null, // store image file
    preview: ""  // store image preview
  });

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    if (name === "image" && files[0]) {
      const file = files[0];
      setNewProduct({
        ...newProduct,
        image: file,
        preview: URL.createObjectURL(file), // ✅ create preview
      });
    } else {
      setNewProduct({ ...newProduct, [name]: value });
    }
  };

  const handleAdd = () => {
    if (!newProduct.name || !newProduct.price || !newProduct.image) {
      alert("Please fill all fields");
      return;
    }

    // ✅ Add product with preview image
    addProduct({
      ...newProduct,
      id: Date.now(),
      image: newProduct.preview, // show preview instead of file object
    });

    // reset form
    setNewProduct({ name: "", price: "", image: null, preview: "" });
  };

  return (
    <div className="admin-container">
      <h1>Admin Panel</h1>

      {/* Add Product Form */}
      <div className="admin-form">
        <input
          type="text"
          name="name"
          placeholder="Product Name"
          value={newProduct.name}
          onChange={handleChange}
        />
        <input
          type="number"
          name="price"
          placeholder="Product Price"
          value={newProduct.price}
          onChange={handleChange}
        />
        <input
          type="file"
          name="image"
          accept="image/*"
          onChange={handleChange}
        />

        {/* ✅ Image Preview */}
        {newProduct.preview && (
          <img
            src={newProduct.preview}
            alt="preview"
            style={{ width: "120px", height: "120px", marginTop: "10px" }}
          />
        )}

        <button onClick={handleAdd}>Add Product</button>
      </div>

      {/* Product List */}
      <div className="admin-products">
        {products.map((product) => (
          <div className="admin-product-card" key={product.id}>
            <img
              src={product.image}
              alt={product.name}
              style={{ width: "120px", height: "120px", objectFit: "cover" }}
            />
            <h3>{product.name}</h3>
            <p>₹{product.price}</p>
            <button onClick={() => deleteProduct(product.id)}>Delete</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AdminPage;
