import React, { useContext } from 'react'
import './Cart.css'
import { ShopContext } from '../Context/ShopContext'
import remove from '../Components/Assest/Ecommerce_Frontend_Assets/Assets/cart_cross_icon.png'

export const Cart = () => {
  const { all_product, cartItems, addToCart, removeFromCart, updateCartQuantity } = useContext(ShopContext);

  return (
    <div className='cart'>
      <div className="main">
        <p>Products</p>
        <p>Title</p>
        <p>Price</p>
        <p>Quantity</p>
        <p>Total</p>
        <p>Remove</p>
      </div>
      <hr />
      {all_product.map((e) => {
        if (cartItems[e.id] > 0) {
          return (
            <div key={e.id}>
              <div className="format">
                <img src={e.image} alt="" className='carticon_product_icon' />
                <p>{e.name}</p>
                <p>${e.new_price}</p>

                {/* Quantity Editor */}
                <div className="quantity-control">
                  <button onClick={() => updateCartQuantity(e.id, cartItems[e.id] - 1)}>-</button>
                  <input 
                    type="number" 
                    value={cartItems[e.id]} 
                    onChange={(event) => updateCartQuantity(e.id, Number(event.target.value))}
                  />
                  <button onClick={() => updateCartQuantity(e.id, cartItems[e.id] + 1)}>+</button>
                </div>

                <p>${e.new_price * cartItems[e.id]}</p>
                <img src={remove} onClick={() => removeFromCart(e.id)} alt="Remove" className='remove-icon' />
              </div>
              <hr />
            </div>
          )
        }
        return null;
      })}
    </div>
  )
}
