import React, { useState } from 'react'
import './Product.css'
import star from '../Assest/Ecommerce_Frontend_Assets/Assets/star_icon.png';
import startdull from '../Assest/Ecommerce_Frontend_Assets/Assets/star_dull_icon.png';
import defaultShirt from '../Assest/Ecommerce_Frontend_Assets/Assets/all_product';

const ProductDis = ({ product }) => {
  const safeProduct = product || {
    name: "Default Shirt",
    image: defaultShirt,
    category: "Clothes"
  };

  const sizes = ["S", "M", "L", "XL", "XXL", "XXXL"];
  const [selectedSize, setSelectedSize] = useState(null);

  return (
    <div className='productdis'>
      {/* LEFT IMAGES */}
      <div className="left">
        <div className="img-list">
          <img src={safeProduct.image} alt={safeProduct.name} />
          <img src={safeProduct.image} alt={safeProduct.name} />
          <img src={safeProduct.image} alt={safeProduct.name} />
          <img src={safeProduct.image} alt={safeProduct.name} />
        </div>
        <div className="img">
          <img className='productdis-main-img' src={safeProduct.image} alt={safeProduct.name} />
        </div>
      </div>

      {/* RIGHT DETAILS */}
      <div className="right">
        <h2>{safeProduct.name}</h2>

        {/* STARS */}
        <div className="star">
          <img src={star} alt="star" />
          <img src={star} alt="star" />
          <img src={star} alt="star" />
          <img src={star} alt="star" />
          <img src={startdull} alt="star dull" />
          <p>(122)</p>
        </div>

        {/* SIZE SELECTION */}
        <div className="sizes">
          <h3>Select Size:</h3>
          <div className="size-options">
            {sizes.map((size) => (
              <button
                key={size}
                className={`size-btn ${selectedSize === size ? "active" : ""}`}
                onClick={() => setSelectedSize(size)}
              >
                {size}
              </button>
            ))}
          </div>
        </div>
      </div>
    </div>
  )
}

export default ProductDis
