import React, { useContext } from 'react';
import './CSS/Shopcat.css';
import Item from '../Components/Item/Item';
import { ShopContext } from '../Context/ShopContext';
import drop from '../Components/Assest/Ecommerce_Frontend_Assets/Assets/dropdown_icon.png';

const Shopcat = (props) => {
  const { all_product } = useContext(ShopContext);

  // Filter products by category (case-insensitive)
  const filteredProducts = all_product.filter(item =>
    item.category.toLowerCase().includes(props.category.toLowerCase())
  );

  return (
    <div className="shop-cat">
      {/* Banner */}
      <img src={props.banner} alt={props.category} />

      {/* Index + Sort */}
      <div className="shopcat-index">
        <p style={{ fontWeight: 600 }}>
          <span style={{ fontWeight: 600 }}>
            Showing 1-{filteredProducts.length}
          </span>{' '}
          out of {all_product.length} products
        </p>
        <div className="shopcat-sort" style={{ fontWeight: 600 }}>
          Sort by <img src={drop} alt="sort" />
        </div>
      </div>

      {/* Products */}
      <div className="shopcat-product">
        {filteredProducts.map((item, i) => (
          <Item
            key={i}
            id={item.id}
            name={item.name}
            image={item.image}
            new_price={item.new_price}
            old_price={item.old_price}
          />
        ))}
      </div>
    </div>
  );
};

export default Shopcat;
