import React, { createContext, useState } from "react";
// ðŸ‘‡ Import your products from your file
import { products } from "../Data";   // <-- change path if your file is in src/Components or src/assets

export const ShopContext = createContext();

export const ShopProvider = ({ children }) => {
  // Products from your file
  const [productList, setProductList] = useState(products);

  // Cart state
  const [cartItems, setCartItems] = useState({});

  // Add to cart
  const addToCart = (id) => {
    setCartItems((prev) => ({
      ...prev,
      [id]: (prev[id] || 0) + 1,
    }));
  };

  // Remove from cart
  const removeFromCart = (id) => {
    setCartItems((prev) => {
      const updated = { ...prev };
      if (updated[id] > 1) {
        updated[id] -= 1;
      } else {
        delete updated[id];
      }
      return updated;
    });
  };

  // Admin: add new product
  const addProduct = (newProduct) => {
    setProductList((prev) => [...prev, { id: Date.now(), ...newProduct }]);
  };

  // Admin: delete product
  const deleteProduct = (id) => {
    setProductList((prev) => prev.filter((p) => p.id !== id));
  };

  return (
    <ShopContext.Provider
      value={{
        products: productList, // âœ… use your imported products
        cartItems,
        addToCart,
        removeFromCart,
        addProduct,
        deleteProduct,
      }}
    >
      {children}
    </ShopContext.Provider>
  );
};
