// src/Context/ShopContext.js
import React, { createContext, useState } from "react";

export const ShopContext = createContext();

const ShopContextProvider = ({ children }) => {
  const [cartItems, setCartItems] = useState({});

  // Add to Cart
  const addToCart = (id, size = "M") => {
    setCartItems((prev) => {
      const current = prev[id] || { quantity: 0, size: size };
      return {
        ...prev,
        [id]: {
          ...current,
          quantity: current.quantity + 1,
        },
      };
    });
  };

  // Remove from Cart
  const removeFromCart = (id) => {
    setCartItems((prev) => {
      if (!prev[id]) return prev;
      const updatedQuantity = prev[id].quantity - 1;
      if (updatedQuantity <= 0) {
        const { [id]: _, ...rest } = prev; // remove completely
        return rest;
      }
      return {
        ...prev,
        [id]: { ...prev[id], quantity: updatedQuantity },
      };
    });
  };

  // Update Size
  const updateSize = (id, size) => {
    setCartItems((prev) => ({
      ...prev,
      [id]: {
        ...prev[id],
        size,
      },
    }));
  };

  // ✅ Update Quantity
  const updateCartQuantity = (id, quantity) => {
    setCartItems((prev) => ({
      ...prev,
      [id]: {
        ...prev[id],
        quantity: parseInt(quantity, 10),
      },
    }));
  };

  return (
    <ShopContext.Provider
      value={{
        cartItems,
        addToCart,
        removeFromCart,
        updateSize,
        updateCartQuantity, // ✅ make sure this is included
      }}
    >
      {children}
    </ShopContext.Provider>
  );
};

export default ShopContextProvider;
