import React, { createContext, useState } from "react";

export const ShopContext = createContext();

export const ShopProvider = ({ children }) => {
  const [cartItems, setCartItems] = useState({});

  // Example structure: cartItems = { 1: { quantity: 2, size: "M" } }

  const addToCart = (id, size = "S") => {
    setCartItems((prev) => {
      const item = prev[id] || { quantity: 0, size };
      return {
        ...prev,
        [id]: {
          ...item,
          quantity: item.quantity + 1,
        },
      };
    });
  };

  const removeFromCart = (id) => {
    setCartItems((prev) => {
      const updated = { ...prev };
      delete updated[id];
      return updated;
    });
  };

  const updateSize = (id, size) => {
    setCartItems((prev) => ({
      ...prev,
      [id]: {
        ...prev[id],
        size,
      },
    }));
  };

  // ✅ Update quantity
  const updateCartQuantity = (id, quantity) => {
    setCartItems((prev) => ({
      ...prev,
      [id]: {
        ...prev[id],
        quantity: Number(quantity), // ensure it's a number
      },
    }));
  };

  return (
    <ShopContext.Provider
      value={{
        cartItems,
        addToCart,
        removeFromCart,
        updateSize,
        updateCartQuantity,
      }}
    >
      {children}
    </ShopContext.Provider>
  );
};
